export default {
  name: 'custom-map',
  components: {
    LMap: LMap,
    LTileLayer: LTileLayer,
    ToolBarMap: ToolBarMap // FormDistrict,
    // FormCity,
    // FormRoute,
    // FormHome,

  },
  data: function data() {
    return {
      url: "http://{s}.tile.osm.org/{z}/{x}/{y}.png",
      zoom: 11,
      center: [40.288405, 69.644229],
      bounds: null
    };
  },
  methods: {
    zoomUpdated: function zoomUpdated(zoom) {
      this.zoom = zoom;
    },
    centerUpdated: function centerUpdated(center) {
      this.center = center;
    },
    boundsUpdated: function boundsUpdated(bounds) {
      this.bounds = bounds;
    },
    zoomPlus: function zoomPlus() {
      this.zoom++;
    },
    zoomMinus: function zoomMinus() {
      this.zoom--;
    },
    editPolygon: function editPolygon() {
      var map = this.$refs.map.mapObject;
      console.log(map);
      var drawnItems = new window.L.FeatureGroup();
      map.addLayer(drawnItems);
    }
  }
};
