import Vue from 'vue';
export default {
  name: 'ContextMenu',
  props: ['display', 'top', 'left'],
  data: function data() {
    return {
      show: false // affect display of context menu

    };
  },
  computed: {
    // get position of context menu
    style: function style() {
      return {
        top: this.top + 'px',
        left: this.left + 'px'
      };
    }
  },
  methods: {
    // closes context menu 
    close: function close() {
      this.show = false;
      this.left = 0;
      this.top = 0;
    },
    open: function open(evt) {
      var _this = this;

      // updates position of context menu 
      this.left = evt.pageX || evt.clientX;
      this.top = evt.pageY || evt.clientY; // make element focused 
      // @ts-ignore

      Vue.nextTick(function () {
        return _this.$el.focus();
      });
      this.show = true;
    }
  }
};
